import React, { useState, useEffect } from 'react';
import Axios from 'axios';

import { Menu } from './export';

import userImage from '../../images/user.png';
import nouser from '../../images/users-not-found.png';

const Index = () => {
   const [users, setUsers] = useState([]);
   const [errorDB, setErrorDB] = useState(false);

   useEffect(() => {
      const gettingData = async () => {
         try {
            const gettinData = await Axios.get(
               'http://localhost:8081/api/get-data'
            ).then((response) => {
               setUsers(response.data);
            });
         } catch (error) {
            setErrorDB(true);
         }
      };
      gettingData();
   }, []);

   const _ = require('lodash');
   let total = _.sumBy(users, (h) => h.haircut);

   return (
      <div>
         <Menu />
         <div className="mt-6">
            <h2 className="font-windsong text-5xl lg:text-6xl text-center">
               Welcome to your business
            </h2>
         </div>
         <div className="flex items-center justify-center my-6">
            <h2 className="lg:text-3xl text-xl text-center">
               Cortes realizados en el día: {total}
            </h2>
         </div>
         <div className="flex flex items-center justify-center flex-col md:flex-row lg:flex-row lg:flex-wrap md:flex-wrap">
            {users.length > 0 ? (
               users.map((user) => {
                  if (user.haircut > 0) {
                     return (
                        <div
                           className="flex items-center flex-col p-3.5 my-5 shadow-md font-lora font-medium lg:mx-12 md:mx-6 w-72"
                           key={user.id}
                        >
                           <img
                              className="w-36 my-5"
                              src={userImage}
                              alt="User profile"
                           />
                           <p className="text-lg mb-3.5 text-center">
                              {user.name} {user.lastname}
                           </p>
                           <div className="flex items-center justify-between w-48">
                              <p>Cortes realizados</p>
                              <p>{user.haircut}</p>
                           </div>
                        </div>
                     );
                  } else {
                     return <div className="hidden" key={user.id}></div>;
                  }
               })
            ) : (
               <div className="flex items-center justify-center flex-col">
                  <span className="mt-12 mb-3.5 font-lora lg:text-xl">
                     Aun no se han registrado barberos
                  </span>
                  <img
                     alt="There are no users on the system"
                     className="w-4/6 md:w-11/12"
                     src={nouser}
                  />
               </div>
            )}
         </div>
         {errorDB ? (
            <p className="text-center text-red-600">Server down</p>
         ) : (
            false
         )}
      </div>
   );
};

export default Index;

